import com.example.infohub_telas.viewmodel.InfoCashViewModel
import com.example.infohub_telas.viewmodel.InfoCashUiState
import com.example.infohub_telas.ui.theme.*

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun TelaInfoCash(navController: NavController) {
    val hubCoinData = HubCoinData.getDefault()
    Box(
        modifier = Modifier
            .fillMaxSize()
            .background(BackgroundGray)
    ) {
        Column(
            modifier = Modifier.fillMaxSize()
        ) {
            // Top Bar com gradiente
            Box(
                modifier = Modifier
                    .fillMaxWidth()
                    .height(80.dp)
                    .background(
                        brush = Brush.horizontalGradient(
                            colors = listOf(PrimaryOrange, SecondaryOrange)
                        )
                    )
            ) {
                Row(
                    modifier = Modifier
                        .fillMaxSize()
                        .padding(horizontal = 16.dp),
                    verticalAlignment = Alignment.CenterVertically
                ) {
                    IconButton(
                        onClick = { navController.popBackStack() }
                    ) {
                        Icon(
                            imageVector = Icons.AutoMirrored.Filled.ArrowBack,
                            contentDescription = "Voltar",
                            tint = Color.White,
                            modifier = Modifier.size(28.dp)
                        )
                    }

                    Spacer(modifier = Modifier.width(12.dp))

                    // Logo do InfoCash
                    Surface(
                        modifier = Modifier.size(48.dp),
                        shape = RoundedCornerShape(24.dp),
                        color = Color.White
                    ) {
                        Box(
                            modifier = Modifier.fillMaxSize(),
                            contentAlignment = Alignment.Center
            // Content baseado no estado da UI
            when (uiState) {
                is InfoCashUiState.Loading -> {
                    Box(
                        modifier = Modifier
                            .weight(1f)
                            .fillMaxWidth(),
                        contentAlignment = Alignment.Center
                    ) {
                        CircularProgressIndicator(color = PrimaryOrange)
                    }
                }

                is InfoCashUiState.Error -> {
                    Box(
                        modifier = Modifier
                            .weight(1f)
                            .fillMaxWidth()
                            .padding(16.dp),
                        contentAlignment = Alignment.Center
                    ) {
                        Column(
                            horizontalAlignment = Alignment.CenterHorizontally
                        ) {
                            Text(
                                text = "Ops! Algo deu errado",
                                fontSize = 18.sp,
                                fontWeight = FontWeight.Bold,
                                color = OnSurfaceGray
                            )
                            Spacer(modifier = Modifier.height(8.dp))
                            Text(
                                text = uiState.message,
                                fontSize = 14.sp,
                                color = OnSurfaceGray,
                                textAlign = TextAlign.Center
                            )
                            Spacer(modifier = Modifier.height(16.dp))
                            Button(
                                onClick = { viewModel.recarregarDados(userId) },
                                colors = ButtonDefaults.buttonColors(containerColor = PrimaryOrange)
                            ) {
                                Text("Tentar novamente")
                            }
                        }
                    }
                }

                is InfoCashUiState.Success -> {
                    LazyColumn(
                        modifier = Modifier
                            .weight(1f)
                            .padding(16.dp),
                        verticalArrangement = Arrangement.spacedBy(16.dp)
                    ) {
                        item {
                            // Card InfoCash Status com dados da API
                            InfoCashStatusCardApi(uiState.saldoInfoCash)
                        }

                        item {
                            // Card Conquistas
                            ConquistasCard()
                        }

                        item {
                            // Card Como Ganhar HubCoins
                            ComoGanharHubCoinsCard()
                        }

                        item {
                            // Card Comunidade
                            ComunidadeCard()
                        }

                        // Espaçamento para o menu inferior
                        item {
                            Spacer(modifier = Modifier.height(80.dp))
                        }
                    }

                item {
                    // Card Conquistas
                    ConquistasCard()
                }

                item {
                    // Card Como Ganhar HubCoins
                    ComoGanharHubCoinsCard()
                }

                item {
                    // Card Comunidade
                    ComunidadeCard()
                }

                // Espaçamento para o menu inferior
                item {
                    Spacer(modifier = Modifier.height(80.dp))
                }
            }
        }

        // Menu inferior
        Box(
            modifier = Modifier
                .align(Alignment.BottomCenter)
                .background(Color.White)
        ) {
            BottomMenu(navController = navController)
        }

        // Botão de chat flutuante
        FloatingActionButton(
            onClick = { /* TODO: Implementar chat */ },
            modifier = Modifier
                .align(Alignment.BottomEnd)
                .padding(end = 16.dp, bottom = 100.dp),
            containerColor = PrimaryOrange
        ) {
            Icon(
                imageVector = Icons.AutoMirrored.Filled.Chat,
                contentDescription = "Chat",
                tint = Color.White
            )
        }
    }
}

@Composable
