                    .fillMaxWidth()
                    .background(
                        color = BackgroundGray,
                        shape = RoundedCornerShape(12.dp)
                    )
                    .padding(16.dp),
                verticalAlignment = Alignment.CenterVertically
            ) {
                Surface(
                    modifier = Modifier.size(40.dp),
                    shape = RoundedCornerShape(20.dp),
                    color = PrimaryOrange
                ) {
                    Box(
                        modifier = Modifier.fillMaxSize(),
                        contentAlignment = Alignment.Center
                    ) {
                        Text(
                            text = "",
                            fontSize = 20.sp
                        )
                    }
                }

                Spacer(modifier = Modifier.width(12.dp))

                Column(modifier = Modifier.weight(1f)) {
                    Text(
                        text = "HubCoin",
                        fontSize = 16.sp,
                        fontWeight = FontWeight.Bold
                    )
                    Text(
                        text = saldoInfoCash.getNivelTexto(),
                        fontSize = 12.sp,
                        color = OnSurfaceGray
                    )
                }

                // Saldo do HubCoin vindo da API
                Text(
                    text = saldoInfoCash.getSaldoComVirgula(),
                    fontSize = 20.sp,
                    fontWeight = FontWeight.ExtraBold,
                    color = PrimaryOrange
                )
            }

            Spacer(modifier = Modifier.height(12.dp))

            // Barra de progresso
            LinearProgressIndicator(
                progress = { saldoInfoCash.getProgressoAtual() },
                modifier = Modifier
                    .fillMaxWidth()
                    .height(8.dp)
                    .clip(RoundedCornerShape(4.dp)),
                color = PrimaryOrange,
                trackColor = BackgroundGray
            )

            Spacer(modifier = Modifier.height(8.dp))

            // Mensagem do próximo nível
            Text(
                text = saldoInfoCash.getMensagemProximoNivel(),
                fontSize = 12.sp,
                color = OnSurfaceGray
            )

            Spacer(modifier = Modifier.height(12.dp))

            // Informação da última atualização
            Row(
                verticalAlignment = Alignment.CenterVertically
            ) {
                Icon(
                    imageVector = Icons.Default.Schedule,
                    contentDescription = "Última atualização",
                    tint = OnSurfaceGray,
                    modifier = Modifier.size(16.dp)
                )
                Spacer(modifier = Modifier.width(4.dp))
                Text(
                    text = "Atualizado recentemente",
                    fontSize = 10.sp,
                    color = OnSurfaceGray
                )
            }
        }
    }
}

